import scala.collection.mutable

object Day6 extends App {

  val sequence = ""

  def hasUniqueChars(str: String, index: Int, numUnique: Int): Boolean = {
    val set = mutable.Set[Char]()
    for (x <- index until (index + numUnique)) {
      set.add(str(x))
    }
    set.size == numUnique
  }

  def findStartOfUniqueChunk(string: String, size: Int): Int = {
    for (x <- 0 until (string.length - 3)) {
      if hasUniqueChars(string, x, size) then return x + size
    }
    -1
  }

  def part1(): Unit = {
    println(findStartOfUniqueChunk(sequence, 4))
  }

  part1()

  def part2(): Unit = {
    println(findStartOfUniqueChunk(sequence, 14))
  }

  part2()

}
